import React, { Component } from 'react';
import {
  IconButton,
  withTheme,
  Title,
  Button,
  Divider,
  Surface,
  List,
  Dialog,
  Portal,
  TextInput,
  Subheading,
} from 'react-native-paper';
import { StyleSheet, View, Dimensions, FlatList, Alert } from 'react-native';
import { connect } from 'react-redux';
import isEmpty from 'lodash/isEmpty';
import values from 'lodash/values';
import BottomSheet from 'reanimated-bottom-sheet';
import Animated from 'react-native-reanimated';
import Share from 'react-native-share';
import LinearGradient from 'react-native-linear-gradient';
import { TouchableWithoutFeedback } from 'react-native-gesture-handler';
import { addToQueue } from '../../actions/playerState';
import { deletePlaylist, renamePlaylist } from '../../actions/realmAction';
import TrackContainer from '../../containers/TrackContainer';
import DefaultImage from '../../components/DefaultImage';
import Screen from '../../components/Screen';
import log, { logEvent } from '../../utils/logging';

// fix on click issue
// {"accent": "#03dac6", "backdrop": "rgba(0, 0, 0, 0.5)", "background": "#121212", "disabled": "rgba(255, 255, 255, 0.38)", "error": "#CF6679", "notification": "#ff80ab", "onBackground": "#FFFFFF", "onSurface": "#FFFFFF", "placeholder": "rgba(255, 255, 255, 0.54)", "primary": "#BB86FC", "surface": "#121212", "text": "#ffffff"}

class Collection extends Component {
  static navigationOptions = ({ navigation }) => {
    // header: null
    return {
      headerTitle: navigation.getParam('playlist').name,
      headerRight: (
        <IconButton
          icon="dots-vertical"
          onPress={navigation.getParam('openMenu')}
        />
      ),
    };
  };

  constructor(props) {
    super(props);
    this.bs = React.createRef();
    this.sheetOpenValue = new Animated.Value(1);
    this.state = {
      visible: false,
      playlist: props.navigation.getParam('playlist'),
    };
  }

  componentDidMount() {
    const { navigation } = this.props;
    navigation.setParams({ openMenu: this.openBottomSheet });
  }

  addToQueue = () => {
    const { addToQueue } = this.props;
    const { playlist } = this.state;
    const { songs } = playlist;
    addToQueue(values(songs));
  };

  deletePlaylist = () => {
    const {
      playlist: { id, name },
    } = this.state;
    const { navigation } = this.props;

    if (id) {
      Alert.alert(
        'Delete playlist',
        `Are you sure you want to delete ${name}`,
        [
          {
            text: 'NO',
            onPress: () => log('Cancel Pressed'),
            style: 'cancel',
          },
          {
            text: 'YES',
            onPress: () => {
              deletePlaylist(id);
              navigation.goBack();
            },
          },
        ],
        { cancelable: false },
      );
    }
    this.bs.current.snapTo(1);
  };

  renamePlaylist = () => {
    const {
      playlist: { id, name },
    } = this.state;
    renamePlaylist(id, name);
    this.hideDialog();
  };

  sharePlaylist = () => {
    this.bs.current.snapTo(1);
    const { playlist } = this.state;
    const options = {
      title: 'Share file',
      message: `Listen to ${playlist.name} playlist on Serenity`,
      subject: 'Checkout the playlist',
      url:
        'data:image/png;base64,',
      failOnCancel: false,
    };
    Share.open(options)
      .then(res => {
        log(res);
      })
      .catch(err => {
        log(err);
      });
  };

  onChangeText = text => {
    this.setState({
      playlistName: text,
    });
  };

  hideDialog = () => {
    this.setState({
      visible: false,
    });
  };

  showDailog = () => {
    this.setState({
      visible: true,
    });
    this.bs.current.snapTo(1);
  };

  openBottomSheet = () => {
    this.bs.current.snapTo(0);
  };

  renderInner = () => {
    const {
      theme: { colors },
    } = this.props;
    const { playlist } = this.state;
    const { name, owner, songs } = playlist;
    return (
      <View style={styles.panel}>
        <LinearGradient
          colors={['rgba(0,0,0, .9)', colors.background, colors.surface]}
          style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}
        >
          <TouchableWithoutFeedback
            onPress={() => this.bs.current.snapTo(1)}
            style={{
              flex: 1,
              justifyContent: 'center',
              alignItems: 'center',
              width: Dimensions.get('window').width,
            }}
          >
            <DefaultImage style={styles.artCover} />
            <Title>{name}</Title>
            <Subheading>{`by ${owner}`}</Subheading>
          </TouchableWithoutFeedback>
        </LinearGradient>
        <Surface>
          <TouchableWithoutFeedback onPress={this.addToQueue}>
            <List.Item
              title="Play All"
              left={props => (
                <List.Icon {...props} icon="play-circle-outline" />
              )}
            />
          </TouchableWithoutFeedback>
          {owner !== 'You' ? (
            <TouchableWithoutFeedback
              onPress={() => logEvent('playlist', 'playlist liked')}
            >
              <List.Item
                title="like"
                left={props => <List.Icon {...props} icon="heart" />}
              />
            </TouchableWithoutFeedback>
          ) : (
            <View>
              <TouchableWithoutFeedback onPress={this.deletePlaylist}>
                <List.Item
                  title="Delete Playlist"
                  left={props => <List.Icon {...props} icon="close" />}
                />
              </TouchableWithoutFeedback>
              {songs.length < 1 ? (
                false
              ) : (
                <TouchableWithoutFeedback onPress={this.renamePlaylist}>
                  <List.Item
                    title="Edit Playlist"
                    left={props => (
                      <List.Icon {...props} icon="playlist-edit" />
                    )}
                  />
                </TouchableWithoutFeedback>
              )}
              <TouchableWithoutFeedback onPress={this.showDailog}>
                <List.Item
                  title="Rename Playlist"
                  left={props => <List.Icon {...props} icon="playlist-edit" />}
                />
              </TouchableWithoutFeedback>
            </View>
          )}

          <TouchableWithoutFeedback onPress={this.sharePlaylist}>
            <List.Item
              title="Share"
              left={props => <List.Icon {...props} icon="share" />}
            />
          </TouchableWithoutFeedback>
        </Surface>
      </View>
    );
  };

  render() {
    const { playlist, visible, playlistName } = this.state;
    const { name, owner, songs } = playlist;

    return (
      <Screen>
        <Portal>
          <Animated.View
            style={[
              {
                flex: 1,
                backgroundColor: 'rgba(0,0,0, .7)',
                ...StyleSheet.absoluteFillObject,
              },
              {
                opacity: Animated.cond(
                  Animated.greaterOrEq(this.sheetOpenValue, 0.95),
                  0,
                  1,
                ),
              },
            ]}
            pointerEvents="none"
          />
          <BottomSheet
            ref={this.bs}
            snapPoints={['100%', 0]}
            renderContent={this.renderInner}
            initialSnap={1}
            callbackNode={this.sheetOpenValue}
          />
        </Portal>
        <Portal>
          <Dialog visible={visible} onDismiss={this.hideDialog}>
            <Dialog.Title>Renaming</Dialog.Title>
            <Dialog.Content>
              <TextInput
                mode="outlined"
                label="Playlist Name"
                value={playlistName}
                onChangeText={this.onChangeText}
              />
            </Dialog.Content>
            <Dialog.Actions>
              <Button onPress={this.hideDialog}>Cancel</Button>
              <Button onPress={this.renamePlaylist}>Rename</Button>
            </Dialog.Actions>
          </Dialog>
        </Portal>
        <View style={styles.scrollViewContent}>
          <FlatList
            ListHeaderComponent={() => (
              <View>
                <View style={styles.coverContainer}>
                  <DefaultImage style={styles.artCover} />
                </View>
                <View style={styles.titleContainer}>
                  <Title>{name}</Title>
                  <Subheading>{`by ${owner}`}</Subheading>
                </View>
                {isEmpty(songs) ? (
                  <View style={{ flex: 1, margin: 16 }}>
                    <Title style={{ textAlign: 'center' }}>
                      Let's find some songs for your playlist
                    </Title>
                    <Button>Find songs</Button>
                  </View>
                ) : (
                  <View style={styles.buttonContainer}>
                    <Button mode="contained" onPress={this.addToQueue}>
                      Play All
                    </Button>
                  </View>
                )}
              </View>
            )}
            data={songs}
            renderItem={({ item }) => <TrackContainer track={item} />}
            ItemSeparatorComponent={() => <Divider inset />}
            keyExtractor={(item, index) => index.toString()}
            ListFooterComponent={() => <View style={{ height: 100 }} />}
          />
          <View style={{ height: 100 }} />
        </View>
      </Screen>
    );
  }
}

export default connect(
  null,
  { addToQueue },
)(withTheme(Collection));

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  scrollViewContent: {
    marginTop: 10,
  },
  coverContainer: {
    justifyContent: 'center',
    alignItems: 'center',
    elevation: 4,
  },
  buttonContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-around',
    marginBottom: 8,
  },
  artCover: { width: 200, height: 200, elevation: 4, borderRadius: 12 },
  titleContainer: {
    alignItems: 'center',
    justifyContent: 'center',
    margin: 10,
  },
  panel: {
    height: '100%',
    paddingTop: 20,
    borderTopLeftRadius: 20,
    borderTopRightRadius: 20,
    // elevation: 12,
    zIndex: 1000,
  },
});
